INSERT INTO public.course (id, cover_url, description, ends_at, internal_rating, is_advanced, is_archived, is_indefinite, short_description, starts_at, title, url, c_course_provider_id) VALUES (96, 'https://248006.selcdn.ru/LandGen/desktop_2_50f99ed1fde5fb15fb167a2dee353f32d6c0b727.webp', '"Вы научитесь программировать на JavaScript, работать с фреймворками Selenium Webdriver и Cypress, тестировать пользовательские интерфейсы и настраивать CI. Соберёте портфолио, сможете претендовать на повышение или работу в крупной IT-компании. Чему вы научитесь
